function DATA = ClearSelections(DATA, force, setcurrent)%wipe the list of selected probes%Unless force is set, this may depend on GUI settings/state%setcurruent is [eid probe], eid is index in exptid    if sum(setcurrent) > 0        DATA.selectprobe(DATA.currentpoint(1),DATA.currentpoint(2)) = 1;        if length(setcurrent) == 2            DATA.currentpoint = setcurrent;        end    end    DATA.selectprobe = zeros(length(DATA.exptid),DATA.nprobes);