function DATA = CompareShapes(DATA, type)        spk = 1;    ids = [];    if ~isfield(DATA,'nprobes')        DATA.nprobes = 24;    end    PC.SetFigure(DATA,DATA.tag.clusters);    clf;    if strmatch(type,{'exptlines' 'exptimage'})        DATA = PC.PlotShapes(DATA, type);    else        Clusters = getappdata(DATA.toplevel,'Clusters');        for j = 1:DATA.nprobes            subplot(4,6,j);            if type == 1                PC.CompareShape(DATA, Clusters, j);            elseif type == 2                PC.PlotShape(DATA,j);            end            drawnow;        end    end