function  DATA = ConditionalPlotXY(DATA, C, force, varargin)         clid = DATA.currentcluster;j = 1;while j <= length(varargin)    if strncmpi(varargin{j},'cluster',5)        j = j+1;        clid = varargin{j};    end    j = j+1;endif isempty(C)    C = PC.GetCurrentCluster(DATA);ende = DATA.currentpoint(1);p = DATA.currentpoint(2);if DATA.plot.spkxy || force    PC.SetFigure(DATA,DATA.tag.xyplot,'front');    hold off;    PC.PlotClusterXY(DATA,C,'cellid',clid);    PC.AddCellLabels(DATA, e, p,'NW');    %If this is a new Expt/Probe, clear newcut    if e ~= DATA.NewCut.exptid || p ~= DATA.NewCut.probe        DATA.NewCut.exptid = 0;        DATA.NewCut.probe = 0;    end    if DATA.plot.trighist        PC.AddTrigHist(DATA,C,clid);    endendif DATA.plot.autoxy    PC.SetFigure(DATA,DATA.tag.autoxyplot,'front');    hold off;    [C, ~, e]  = PC.CheckClusterLoaded(DATA,DATA.currentpoint(1),'auto');    if e > 0        PC.PlotClusterXY(DATA,C{e}{p},'cellid',clid);    endend