function [d, drop, C] = DistanceMeasure(C, cl, type, varargin)    [C, ok] = PC.GetClusterInfo(C, cl);    zeropad = 0;    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'zeropad',6)            zeropad = 1;        end        j = j+1;    end    drop = NaN;    d = NaN;    if isnumeric(type)        strings = {'ND' '2D' 'XX' '1D' '2Gauss'};        type = strings{type};    end    if ok == 0        d = NaN;    elseif strcmp(type,'1D')         d = C.mahal(4);    elseif strcmp(type,'2D')         d =C.mahal(2);    elseif strcmp(type,'ND')         d =C.mahal(1);    elseif strcmp(type,'2Gauss')        if isfield(C,'fitdprime') %might need to add test of version # when + fitprime was good            d  = max([-C.fitdprime(1) 0]);        end    elseif strcmpi(type,'LRatio') && isfield(C,'lratio')        d = C.lratio;    elseif strcmpi(type,'isolation distance') && isfield(C,'isolation_distance')        d = C.isolation_distance;    elseif strcmpi(type,'myisolation') && isfield(C,'isolation')        d = C.isolation(1);    end    if isfield(C,'dropi') && length(C.dropi) > 2        drop = C.dropi(3);    end    if zeropad && isnan(d)        d = 0;    end                