function h = DrawBox(ex, p, imtype, varargin)%h = DrawBox(ex, p, imtype, varargin) Draw box on image at ex,p%imtype is passed to the callback PC.HitImage,     lx = -0.5;    bw = 0.5;    j = 1;    passon = {};    while j <= length(varargin)        if strcmp(varargin{j},'box2')            lx = 0;        else            passon = {passon{:} varargin{j}};        end        j = j+1;    end        if length(ex) > 1 || length(p) > 1        square = [min(p)+lx max(p)+0.5 max(p)+0.5 min(p)+lx min(p)+lx; min(ex)-0.5 min(ex)-0.5 max(ex)+0.5 max(ex)+0.5 min(ex)-0.5];    else        square = [lx bw bw lx lx; -bw -bw bw bw -bw];        square = [square(1,:)+p; square(2,:)+ex];    end    if iscell(imtype)        h = plot(square(1,:),square(2,:),'k-','buttondownfcn',imtype,'linewidth',2);    else        h = plot(square(1,:),square(2,:),'k-','buttondownfcn',{@PC.HitImage, imtype},'linewidth',2);    end    if length(passon)        set(h,passon{:});    end                        