 function h= DrawLine(E,varargin)        if ~isfield(E,'pos') || isfield(E,'crit')  %pos can get out of data when save from plotclusters            if isfield(E,'version') & E.version > 1.1            xy= xyrotate([E.crit E.crit],get(gca,'ylim'),-E.angle);            else                xy = [E.crit E.crit; get(gca,'ylim')]';            end            E.pos = [xy(1,1), xy(1,2) xy(2,1) xy(2,2);];        endx = [E.pos(1) E.pos(3)];y = [E.pos(2) E.pos(4)];if length(E.pos) > 7    xp = E.pos([5 7]);    yp = E.pos([6 8]);elsexp = [mean(x) + diff(y)/4 mean(x)-diff(y)/4];yp = [mean(y) - diff(x)/4 mean(y)+diff(x)/4];endif isfield(E,'h') && IsDataLine(E.h)    set(E.h(1),'Xdata',x,'Ydata',y);    h = E.h;    if length(E.h) > 1 && ishandle(E.h(2))        set(E.h(2),'Xdata',xp,'Ydata',yp);    endelse    if isfield(E,'h') && isempty(E.h)        fprintf('E.h is empty\n');    end    hold on;    h(1) = plot(real(x),real(y),varargin{:});    h(2) = plot(xp,yp,'r:');    hold off;end