function name = FileName(DATA, ex, probe, type)       smrname = regexprep(DATA.name,'lem/M([0-9]*)','$0/lemM$1');   if strmatch(type,'Expt')       name = [smrname '.' num2str(ex) 'idx.mat'];   elseif strmatch(type,'FullV')       if DATA.probesperfile == 1           name = [DATA.name '/Expt' num2str(ex) '.p' num2str(probe) 'FullV.mat'];       else           name = [DATA.name '/Expt' num2str(ex) 'FullV.mat'];       end   end    