function [eid, cid, clid] = FindCell(DATA, cellid, expts)%[eid, cid, clid] = FindCell(DATA, cellid, expts)%return expts, probes and cluster# for a given cell%eid is a row number, not an expt number    id = find(DATA.CellList == cellid);    [eid, cid, clid] = ind2sub(size(DATA.CellList),id);    [eid, id] = sort(eid);    cid = cid(id);    clid = clid(id);    if nargin > 2 %find cell for particualr expts        id = find(ismember(eid, expts));        eid = eid(id);        cid = cid(id);        clid = clid(id);    end    