function id = FindSpikes(DATA,C, xcl)    xid = [];    e = DATA.currentpoint(1);    p = C.probe(1);    t = C.t.*10000;    Expts = getappdata(DATA.toplevel,'Expts');    e = floor(C.exptno); %exlusion list is by Exp # not row%    e = C.exptid; %but Expts list is as long as rows    if max(xcl) > length(Expts{e}.Trials)        fprintf('Error In Trial Exclusion list\n');    else        for j = 1:length(xcl)            id = find(t > Expts{e}.Trials(xcl(j)).Start(1) & t< Expts{e}.Trials(xcl(j)).End(end));            xid = [xid id];        end    end    id = setdiff(1:length(C.t),xid);