function FixBoundary(DATA, Clusters, e, p)    C = Clusters{e}{p};    xy = xyrotate(C.xy(:,1),C.xy(:,2),C.angle);    fits{1}.params = C.fitdpparams(1,:);    fits{2}.params = C.fitdpparams(2,:);    fits{1}.state.meanlimit = [min(xy(:,1)) C.crit];    fits{2}.state.meanlimit = [C.crit max(xy(:,1))];    fits{1}.amp = C.fitdpparams(1,3);    fits{2}.amp = C.fitdpparams(1,3);%    B = mydip(fits);    B = MyDip(xy(:,1),'idlist',C.clst);    crit = B.x(B.dip(1));    if DATA.plot.hist || DATA.plot.refitgm        oldf = gcf;        PC.SetFigure(DATA,DATA.tag.hist,'front');        hst = PC.PlotClusterHistogram(DATA, C, DATA.plot.refitgm,'cluster', DATA.currentcluster);        plot([crit crit],get(gca,'ylim'));        id = find(hst.x > min(B.x) & hst.x < max(B.x));        if ~isempty(id)        cmax = max(hst.nsp(id));        plot(B.x,B.y .* cmax./max(B.y),'g-');        end        figure(oldf);    end    hold on;    if B.dip(2)./min(B.dip(3:4)) < 0.9 && B.d > 0.1 & B.d < 0.97        xy = xyrotate([crit crit],[minmax(xy(:,2))'],-C.angle);        plot(xy(:,1),xy(:,2),'k-');    end    