function Get2DMaxima(DATA)        mid = DATA.mahaltype;X = squeeze(DATA.mahal(:,:,mid));[a,b,G] = Gauss2D(1,[-3:1:3]);Y = conv2(X,G,'same');PC.SetFigure(DATA,DATA.tag.smoothed);imagesc(Y);xm = diff(sign(diff(Y)));[a,b] = find(xm < 0);ym = diff(sign(diff(Y,1,2)),1,2);[a,b] = find(xm(:,2:end-1) < 0 & ym(2:end-1,:) < 0);hold on;mxs = xm(:,2:end-1) + ym(2:end-1,:)plot(b+1,a+1,'r+');title(sprintf('%d maxima',length(b)));