function [yl, details] = GetYRange(h, xl)    details.result = 0;    if h <= 0 || ~ishandle(h)        yl = get(gca,'ylim');        details.color = 'b';        return;    end    X = get(h,'xdata');    Y = get(h,'ydata');    id = find(X >= xl(1) & X <= xl(2));    details.color = get(h,'color');    if length(id) <= 1        yl = get(gca,'ylim');    else        yl = minmax(Y(id));        if diff(yl) == 0            yl(1) = yl(1)+1;        end        details.result = 1;    end            