function GuiMenu(a,b, type)    DATA = GetDataFromFig(a);    if strcmp(type,'setmahaltype')        DATA.mahaltype = strmatch(GetPopStr(a),{'2D' 'ND' ''  '1D' '2Gauss'});        DATA.mahaltype = deblank(GetPopStr(a));        DATA = PC.CalcDistances(DATA);        F = PC.SetFigure(DATA,DATA.tag.all);        PC.PlotMahalImage(DATA,'distance');    elseif strcmp(type,'setmahalcrit')        DATA.mahalcrit = str2num(get(a,'string'));    elseif strcmp(type,'CallAllVPcs')        PC.CallAllVPcs(DATA, (DATA.currentpoint(1)),DATA.currentpoint(2));    elseif strcmp(type,'checkclusters')        PC.CheckClusters(DATA,'exclusions');        PC.CheckClusters(DATA,'nclusters');        PC.CheckClusters(DATA,'fitspace');        PC.CheckClusters(DATA,'fittimes');        DATA = PC.CheckClusterLineSign(DATA);    end    set(DATA.toplevel,'UserData',DATA);        