function HitPoint(a,b, C, mode, pt)DATA = GetDataFromFig(a);Clusters = getappdata(DATA.toplevel,'Clusters');C = Clusters{DATA.id};fprintf('Probe %d\n',pt);sz = 1;PC.SetFigure(DATA,DATA.tag.onecluster);hold off; plot(C{pt}.xy(:,1),C{pt}.xy(:,2),'.', 'markersize', sz);hold on;yl = get(gca,'ylim');plot([C{pt}.crit(1) C{pt}.crit(1)],yl,'r-');[a,b] = smhist(C{pt}.xy(:,1),100);plot(b,yl(1)+ a.*range(yl)./max(a),'r');title(sprintf('Probe %d',pt));