function HitXYseq(a,b)    DATA = GetDataFromFig(a);    PC.SetTrialList(DATA,{},DATA.currenttrial);    xy = get(gca,'currentpoint');    if isfield(DATA.Expt.Header,'timeoffset')        toff = DATA.Expt.Header.timeoffset;    else        toff = 0;    end    id = find([DATA.Expt.Trials.TrialStart] < (xy(1,1)-toff) .* 10000);    if isempty(id)        id = 1;    end    T = DATA.Expt.Trials(id(end));    fprintf('Trial %d:  %d, id%d\n',id(end),T.Trial,T.id);    PC.SpoolSpikes(DATA,DATA.currentpoint,'ids',id(end))