function LoadAllSpikes(DATA, varargin)        verbose = 1;    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'quiet',5)            verbose = 0;        end        j = j+1;    end    ts = now;    [a,b] = fileparts(DATA.name);    mkn = GetMonkeyName(DATA.name);    if 0    b = regexprep(b,'[GM]([0-9]*)(.*)','M$1');    else    b = regexprep(b,'[GM]([0-9]*)(.*)','$0');    end    [c,d] = fileparts(a);    for k = 1:length(DATA.exptid)        for p = 1:DATA.nprobes            xs = '';            if rem(DATA.exptid(k),1) > 0.001                xs = 'a';            end            name = [DATA.name '/Spikes/' mnk b '.p' num2str(p)  't' num2str(floor(DATA.exptid(k))) xs '.mat'];            if verbose > 0            fprintf('Reading %s at %s\n',name,datestr(now));            end            AllSpikes{k,p} = ReadSpikeFile(name);            AllSpikes{k,p}.probe = p;            if isfield(AllSpikes{k,p},'Header')            if ~isfield(AllSpikes{k,p}.Header,'ctime')                AllSpikes{k,p}.Header.ctime = 0;            end            else                AllSpikes{k,p}.Header.ctime = 0;            end        end    end    fprintf('Spike Load took %.2f\n',mytoc(ts));    setappdata(DATA.toplevel,'AllSpikes',AllSpikes);