    function [Expt, DATA]  = LoadExpt(DATA, e)        Expt = [];        mnk = GetMonkeyName(DATA.name);        smrname = regexprep(DATA.name,[mnk '/([MG][0-9]*)(.*)'],['$0/' mnk '$1']);%        smrname = regexprep(smrname,'jbe/G([0-9]*)(.*)','$0/jbeG$1');        exfile = [smrname '.' sprintf('%d',floor(e)) 'idx.mat'];        if exist(exfile,'file')            precount = 0;            fprintf('Loading %s\n',exfile);            [Trials, Expts] = APlaySpkFile(exfile,'noerrs');            if isempty(Expts)                Expt.Header.exptno = DATA.exptid(e);            else                if length(Expts) > 1                    for j = 1:length(Expts)                        nt(j) = length(Expts{j}.Trials);                    end                    fprintf([sprintf(' %d',nt) '\n']);                    [a,b] = max(nt);                    Expt = FillTrials(Expts{b},'ed');                else                    Expt = FillTrials(Expts{1},'ed');                end                Expt.Header.errs = Trials.errs;                Expt.Header.exptno = e;                DATA.electrodedepth(e) = mean([Expt.Trials.ed]);            end            if isfield(Trials.Comments,'Peninfo') & isfield(Trials.Comments.Peninfo,'probesep')                DATA.probesep(e) = Trials.Comments.Peninfo.probesep;            end            DATA.expnames{e} = Expt2Name(Expt,'addsuff');            DATA.exped(e) = mean(GetEval(Expt,'ed'));            id = find(ismember(DATA.CellDetails.exptids,e));            DATA.trialids{e} = [Expt.Trials.id];            DATA.exptid(e) = GetExptNumber(Expt);        end