function DATA = LoadTrialLists(DATA)    Expts = getappdata(DATA.toplevel,'Expts');    cluster = 1;    exid = DATA.CellDetails.exptids;    for j = 1:size(Expts,1)        Tn = [Expts{j}.Trials.id];        e = find(ismember(exid,GetExptNumber(Expts{j})));        if ~iempty(e)            tdiff = setxor(DATA.CellDetails.trialids{e},Tn);            if length(tdiff) > 0 && e <= size(DATA.CellDetails.excludetrials,1)                fprintf('Expt Trial List Changed (%d) for Expt %d\n',length(tdiff),Expts{j}.Header.exptno);                for k = 1:size(DATA.CellDetails.excludetrials,2)                    nx = length(DATA.CellDetails.excludetrials{e,k,cluster});                    if nx > 0                        fprintf('Probe %d Cluster %d has %d excluded Trials\n',k,cluster,nx);                        [a, id] = ismember(Tn,DATA.CellDetails.excludetrials{e,k,cluster});                    end                end            end            DATA.CellDetails.trialids{e} = Tn;        end    end