function DATA = MarkCurrentCluster(DATA)if strmatch(DATA.plot.alltype,{'template' 'mahal+var'})    PC.SetFigure(DATA, DATA.tag.all);    square = [-.5 0.5 0.5 -0.5 -0.5; -0.5 -0.5 0.5 0.5 -0.5];    hold on;    if ishandle(DATA.markh)        delete(DATA.markh);    end    cmenu = PC.AddContextMenu(DATA,'cellplot');    DATA.markh = plot(square(1,:)+DATA.currentpoint(2),square(2,:)+DATA.currentpoint(1),'k-','linewidth',2);    set(DATA.markh,'buttondownfcn',{@PC.HitImage,1},'uicontextmenu',cmenu);else     DATA.markh = [];end