 function MarkTrialStarts(Expt, ticks, xcl)     h = ishold;     hold on;     yl = get(gca,'ylim');     yl(2) = yl(1) + diff(yl)/8;     if isfield(Expt.Header,'timeoffset')         toff = Expt.Header.timeoffset;     else         toff = 0;     end     for j = 1:length(Expt.Trials)         if ticks             t = Expt.Trials(j).Start(1);         else             t = Expt.Trials(j).Start(1)./10000;         end         t = t+toff;         if ismember(j,xcl)             plot([t t],yl,'m-');         else             plot([t t],yl,'k-');         end     end    if h == 0        hold off;    end    