function missed = MissedCell(DATA, pos)        e = pos(1);    p = pos(2);    fitd = 0;    if isfield(DATA,'GaussFitdp') && size(DATA.GaussFitdp,1) >= e        fitd = DATA.GaussFitdp(e,p,2);        if sum(DATA.gmfitpos(e,p,:)) < 2            fitd = 0;        end    end    d = max(DATA.mahal(e,p,[1 3]));    if PC.isacell(DATA,e,p) == 0 && ((d > 2 && abs(fitd) > 1) || abs(fitd) > 3) && DATA.peakdiff(e,p) == 0 && DATA.dropi(e,p,1) > 1        missed = 1;    else        missed = 0;    end    