function PlotAllCellSpikes(DATA)    square = [-.5 0.5 0.5 -0.5 -0.5; -0.5 -0.5 0.5 0.5 -0.5];    markh = NaN;;    it = findobj('Tag','CellNumberId');    cellid = get(it,'value');    [eid, cid, clid] = PC.FindCell(DATA, cellid);    PC.SetFigure(DATA,DATA.tag.celllist);    spkcolors = mycolors('spkcolors');    Clusters = getappdata(DATA.toplevel,'Clusters');    AllSpikes = PC.CheckAllSpikes(DATA,eid,cid,'allprobes');    for j = 1:length(eid)        PC.SetFigure(DATA,DATA.tag.celllist);        if ishandle(markh)            delete(markh);        end        markh = plot(square(1,:)+cid(j),square(2,:)+eid(j),'g-','linewidth',2);        if isempty(AllSpikes)            PC.SpoolSpikeFile(DATA, eid(j), cid(j));        else            colors = spkcolors;            PC.SetFigure(DATA,DATA.tag.xyplot,'front');            hold off;            if clid(j) ~= 1                np = clid(j)+1;                colors{np} = spkcolors{2};                colors{2} = spkcolors{np};            end            PC.PlotClusterXY(DATA,Clusters{eid(j)}{cid(j)}, 'colors', colors);            drawnow;            stopped = PC.SpoolSpikes(DATA, [eid(j) cid(j)], 'colors', colors);            if stopped                return;            end        end    end    