function PlotAllCellXY(DATA)    Clusters = getappdata(DATA.toplevel,'Clusters');    it = findobj('Tag','CellNumberId');    cellid = get(it,'value');        ts = now;    if DATA.profiling        profile on;    end    id = find(DATA.CellList == cellid);    [eid, cid, clid] = ind2sub(size(DATA.CellList),id);        [eid, id] = sort(eid);    cid = cid(id);    clid = clid(id);    cid = 1+mod(cid-1,DATA.nprobes);    [nr,nc] = Nsubplots(length(eid));    PC.SetFigure(DATA,DATA.tag.allxy);    subplot(1,1,1);    colors = mycolors('spkcolors');    for j = 1:length(eid)        mysubplot(nr,nc,j);        hold off;         plots = PC.PlotClusterXY(DATA,Clusters{eid(j)}{cid(j)},'shorttitle','cellid',clid(j),'allxy');        if clid(j) ~= 1 && length(plots) > 1            np = clid(j)+1;            if length(plots) >= np                set(plots(np),'color','r');                set(plots(2),'color',colors{np});            else                set(plots(2),'color','k');            end        end        if DATA.plot.trighist            PC.AddTrigHist(DATA,Clusters{eid(j)}{cid(j)}, DATA.currentcluster);        end        set(gca,'Xtick',[],'Ytick',[]);        h = get(gca,'title');        xl = get(gca,'xlim');        yl = get(gca,'ylim');        a = get(h,'position');        a(2) = yl(2);        a(1) = mean(xl);        set(h,'position',a,'VerticalAlignment','top');        color = 'b';        if max(Clusters{eid(j)}{cid(j)}.mahal([1 3])) < 3            color = 'r';        elseif max(Clusters{eid(j)}{cid(j)}.mahal([1 3])) < 2            color = 'k';        end                   if length(plots) > 1            ph = plots(2:end);        else            ph = [];        end        set(h,'fontweight','normal','fontsize',12,'color',color);        set(gca,'ButtonDownFcn',{@PC.HitXYPlot, eid(j), cid(j)});        c = get(gca,'Children');        for k = 1:length(c)            if ~ismember(c(k),ph);            set(c(k),'ButtonDownFcn',{@PC.HitXYPlot, eid(j), cid(j)});            end        end        PC.AddCellLabels(DATA, eid(j), cid(j));    end    if DATA.profiling        fprintf('Took %.2f\n',mytoc(ts));        profile viewer;    end    