function PlotAllClusterMean(DATA, varargin)            Clusters = getappdata(DATA.toplevel,'Clusters');    eid = DATA.currentpoint(1);    p = DATA.currentpoint(2);    %[nr,nc] = Nsubplots(length(Clusters{eid}));    [nr,nc] = Nsubplots(DATA.nexpts);    PC.SetFigure(DATA,DATA.tag.allmean);        ha = zeros(1,length(Clusters{eid})); ylim = [inf,-inf];    for j = 1:DATA.nexpts        mysubplot(nr,nc,j);        cla        PC.PlotMeanSpike(Clusters{j}{p},p,1,'allcluster');        for i=1:length(Clusters{j}{p}.next)            if ~isempty(Clusters{j}{p}.next{i})                %PC.PlotMeanSpike(Clusters{eid}{j}.next{i},j,i+1,'allcluster');                PC.PlotMeanSpike(Clusters{j}{p},p,i+1,'allcluster');            end        end        ha(j) = gca;                set(gca,'Xtick',[],'Ytick',[],'YLimMode','auto');        xl = get(gca,'XLim');        yl = get(gca,'YLim');        ylim = [min([ylim(1),yl(1)]),max([ylim(2),yl(2)])];        text(xl(2),yl(1),sprintf('E%dP%d:%s',j,p,DATA.expnames{j}),'horizontalalignment','right','verticalalignment','bottom');        set(gca,'ButtonDownFcn',{@PC.HitXYPlot, j, p});    end    linkaxes(ha,'xy');    set(gca,'YLim',ylim)