function PlotAllExpt(DATA, type)    Clusters = getappdata(DATA.toplevel,'Clusters');    [nr,nc] = Nsubplots(length(Clusters));    PC.SetFigure(DATA,DATA.tag.allexpt);    cr = [];    if strcmp(type,'exptdpim')        for e = 1:length(Clusters)            dprange = [];            for k = length(Clusters{e}):-1:1                dprange(k,:) = minmax(Clusters{e}{k}.MeanSpike.dp(:));            end            crange(e,:) = minmax(dprange(:));        end        cr = [min(crange(:,1)) max(crange(:,2))];    end    for e = 1:length(Clusters)        mysubplot(nr,nc,e);        hold off;         if strmatch(type,{'exptim' 'exptdpim'})             h = PC.PlotProbeMeans(Clusters{e},type,'caxis',cr);            crange(e,:) = caxis;            set(h,'buttondownfcn',{@PC.HitExptPlot,'expt', e})        end        set(gca,'buttondownfcn',{@PC.HitExptPlot,'expt', e},'xtick',[],'ytick',[]);    end    if isempty(cr)    cr = [min(crange(:,1)) max(crange(:,2))];    for e = 1:length(Clusters)        mysubplot(nr,nc,e);        caxis(cr);    end    end