function PlotAllProbeMean(DATA, type, varargin)    if strcmp(type,'AllMeanIm')        type = 'imageonly';    elseif strcmp(type,'AllMean')        type = 'lineonly';    elseif strcmp(type,'AllExptIm')        type = 'exptim';    end            Clusters = getappdata(DATA.toplevel,'Clusters');    eid = DATA.currentpoint(1);    [nr,nc] = Nsubplots(length(Clusters{eid}));    PC.SetFigure(DATA,DATA.tag.allxy);    cmenu = PC.AddCellContextMenu(DATA, 'subplot');    axdata.toplevel = DATA.toplevel;    for j = 1:length(Clusters{eid})        mysubplot(nr,nc,j);        hold off;         PC.PlotMeanSpike(Clusters{eid}{j},0,1,type);        set(gca,'Xtick',[],'Ytick',[]);        h = get(gca,'title');        xl = get(gca,'xlim');        yl = get(gca,'ylim');        a = get(h,'position');        a(2) = yl(2);        a(1) = mean(xl);        set(h,'position',a,'VerticalAlignment','top');        axdata.probe = j;        axdata.eid = eid;        set(gca,'UIContextMenu',cmenu, 'UserData', axdata);    end