function PlotCellSummary(DATA, e, p)    Clusters = LoadCluster(DATA.name, e,'getxy');    Clusters{p}.exptid = e;    DATA.exptid = DATA.CellDetails.exptids;    chspk = [p p-1 p+1];    chspk = chspk(chspk > 0 & chspk <= DATA.nprobes);        Spks = PC.AddSpikes(DATA, {},  e, p);    maxv(p,:) = Spks{e,p}.VRange;    for j = 2:length(chspk)    Spks = PC.AddSpikes(DATA, Spks,  e, chspk(j));    maxv(chspk(j),:) = Spks{e,chspk(j)}.VRange;    end    DATA.voffset =  cumsum(cat(1,maxv(2:end,2), maxv(end,2))) - cumsum(maxv(:,1));    DATA.voffset = DATA.voffset .* 0.7;    F = PC.SetFigure(DATA, 'CellSummary');    m = findobj(F,'Tag','CellList');    c = get(m,'Children');    delete(c);    cellid = unique(DATA.CellList(e,:,:));    cellid = cellid(cellid>0);    for j = 1:length(cellid)        [b,a] = find(squeeze(DATA.CellList(e,:,:)) == cellid(j));        uimenu(m,'Label',sprintf('Cell%d P%dC%d',cellid(j),b,a),'callback',{@PC.NewCellSummary, e, b});    end    subplot('Position',[0.01 0.01 0.5 0.98]);    PC.QuickSpikes(DATA, cat(1,Spks{e,chspk}),Clusters{p});    subplot('Position',[0.52 0.52 0.46 0.46]);    hold off;    PC.PlotClusterPoints(Clusters{p},[],1);    Clusters{p}.down = 0;    PC.DrawEllipse(Clusters{p},'color','r');    set(gca,'xtick',[],'ytick',[]);    PC.AddCellLabels(DATA, e, p);    subplot('Position',[0.52 0.01 0.46 0.46]);    PC.PlotMeanSpike(Clusters{p},p,1,'imageonly');        