    function plots = PlotClusterPoints(C, uid, cid, varargin)        plotgmcid = 0;        finishplot = 0;        clnum = 1;        plots = [];        j = 1;        colors = mycolors('spkcolors');        while j <= length(varargin)            if strncmpi(varargin{j},'colors',6)                j = j+1;                colors = varargin{j};            elseif strncmpi(varargin{j},'finish',5)                finishplot = 1;            elseif strncmpi(varargin{j},'plotgmcid',8)                plotgmcid = 1;            elseif strncmpi(varargin{j},'xydata',6)                j = j+1;                C.xy = varargin{j};            end            j = j+1;        end        if length(cid) > 1            xy = PC.XYSpace(C);            C.xy(:,1) = xy(:,1);            xy = PC.XYSpace(C.next{1});            C.xy(:,2) = xy(:,1);        elseif cid > 1 && length(C.next) >= cid-1 && isfield(C.next{cid-1},'xy')            C.xy = C.next{cid-1}.xy;            if isfield(C.next{cid-1},'triggerset')                trigset = C.next{cid-1}.triggerset;            if trigset > 0                C.clst = C.trigset{trigset}.clst;            end            end        end        if isempty(uid)            uid = 1:size(C.xy,1);        end        axdata = get(gca,'UserData');        plot(C.xy(uid,1),C.xy(uid,2),'.','markersize',1);        hold on;        if plotgmcid && isfield(C,'gmfit2d')            cid = cluster(C.gmfit2d,C.xy);            id = find(cid == clnum+1);        elseif isfield(C,'clst')            id = find(C.clst(uid) == clnum+1);        elseif C.sign < 0            id = find(C.xy(uid,1) <  C.crit(1));        else            id = find(C.xy(uid,1) >  C.crit(1));        end        cells = unique(C.clst(uid));        cells = cells(cells > 0);        if ~isfield(C,'exptid') %called from somewhere else            C.exptid = C.exptno;        end        for j = 1:length(cells)            id = find(C.clst(uid) == cells(j));            if length(id)                plots(j) = plot(C.xy(uid(id),1),C.xy(uid(id),2),'.','markersize',1,'color',colors{cells(j)});                if j > 1                    set(plots(j),'ButtonDownfcn',{@PC.ReplotXY, C.exptid, C.probe(1), cells(j)-1});                end            end        end        set(gca,'UserData',axdata);        