function Expt = PlotCombinedExpt(DATA)[exid, pid] = find(DATA.selectprobe);Clusters = PC.CheckClusterLoaded(DATA,exid);Expts = getappdata(DATA.toplevel,'Expts');clusterid = 1;PC.SetFigure(DATA,DATA.tag.expt);args = {'shown'};    if length(clusterid) == 1        clusterid = ones(size(exid)).* clusterid;    end    h = [];    exptid = DATA.exptid(exid);    for j = 1:length(exid)        E{j} = PC.CountExptSpikes(DATA, Expts{exptid(j)},Clusters{exid(j)}{pid(j)},clusterid(j));    end    Expt = CombineExpts(E);    PlotExpt(Expt,'fbox',args{:});