function h = PlotProbeMeans(C,type, varargin)        mscale = [0 0.5];    maxmahal = 5;    cr = [];    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'caxis',3)            j = j+1;            cr = varargin{j};            mscale(1) = cr(1);            mscale(2) = maxmahal/(cr(2)-cr(1));            mscale(2) = (cr(2)-cr(1))/maxmahal;        end        j = j+1;    end    for j = 1:length(C)        if strcmp(type,'exptim')            nv = size(C{j}.MeanSpike.ms,2);            im(j,1:nv) = C{j}.MeanSpike.ms(j,:);        elseif strcmp(type,'exptdpim')            row = ceil(size(C{j}.MeanSpike.dp,1)/2);            nv = size(C{j}.MeanSpike.dp,2);            im(j,1:nv) = C{j}.MeanSpike.dp(j,:);            dp = min([maxmahal max(C{j}.mahal([1 4]))]);            im(j,end) = (dp+mscale(1)) .*mscale(2);            im(j,end) = (dp.*mscale(2))+mscale(1);        end    end    h = imagesc(im);    if length(cr)        caxis(cr);    end    