function PlotRateCheck(R)    hold off;    if strcmp(R.plottype,'image');        imagesc(R.Im,'buttondownfcn',{@PC.HitImage, 'CellRates'});    else        sym = 'osx+<v>^*ph.osx+<v>^*ph.';        colors = mycolors;        if strcmp(R.plottype,'skew');            x = R.blkff;            y = R.blkskew;        else            x = R.blkff;            y = R.blkcv;        end        hold off;    for j = 1:size(R.blkcv,1);        for k = 1:size(R.blkcv,2);            h = plot(x(j,k),y(j,k),sym(j),'color',colors{k},'buttondownfcn',{@PC.HitExptPoint,j,R.cellid(k)});            if R.warning(j,k)                set(h,'linewidth',2);            end            hold on;        end    end    xlabel('Fano Factor (blockwise)');    if strcmp(R.plottype,'skew');        set(gca,'xscale','log','yscale','lin');        ylabel('Skewness');    else        set(gca,'xscale','log','yscale','log');        ylabel('SD/mean(blockwise)');    endendfgf;