    function PlotShape(DATA, spk)   [Clusters, DATA] = PC.CheckClusterLoaded(DATA, 1:length(DATA.exptid));  for j = 1:length(DATA.strings)      if length(Clusters{j}) >= spk          nv = size(Clusters{j}{spk}.MeanSpike.ms,2);          shape(1:nv,j) = Clusters{j}{spk}.MeanSpike.ms(spk,:);          ci(j) = Clusters{j}{spk}.mahal(1);      end  end  a = minmax(shape(:));  b = minmax(ci);  shape(nv+1,1:length(ci)) = (ci .* diff(a)/diff(b))+a(1);  imagesc(shape);