function PlotTimeRanges(DATA)    X = getappdata(DATA.toplevel,'Clusters');    Expts = getappdata(DATA.toplevel,'Expts');    colors = mycolors;    Clusters = X{DATA.currentpoint(1)};    e = DATA.currentpoint(1);    PC.SetFigure(DATA,DATA.tag.checktimes);    hold off;    for j = 1:length(Clusters)        C = Clusters{j};        plot(Clusters{j}.times,'color',colors{j});        text(length(C.times),C.times(end),sprintf('%d',j),'color',colors{j});        hold on;        xcl = PC.FindExcludedTrials(DATA, e, j,1, C);        if length(xcl)            t = [Expts{e,j}.Trials(xcl).TrialStart]./10000;            plot(length(C.times),t,'.','color',colors{j});        end    end