function nt = PlotTrialSpikes(DATA, nt, varargin)if length(nt) > 1    for j = 1:length(nt)        k= PC.PlotTrialSpikes(DATA, nt(j), varargin{:});        drawnow;    end    nt = k;    return;endif nt < 1     nt = 1;    return;endif  nt > length(DATA.Expt.Trials)    nt = length(DATA.Expt.Trials);    return;endClusters = getappdata(DATA.toplevel,'Clusters');e = DATA.currentpoint(1);AllSpikes = PC.CheckAllSpikes(DATA,e, 1:DATA.nprobes,'allprobes');PC.SpoolAllProbes(DATA, e, AllSpikes(e,:), Clusters{e}, 'Trials' , nt)    