function str = ProbeLabel(p, DATA, cl)if nargin == 2    cl = 1;end    if ~isempty(DATA) && ~isempty(DATA.ArrayConfig) && length(unique(p)) ==1        p = unique(p);        if cl > 1            pstr = sprintf('%d.%d',p,cl);        else            pstr = sprintf('%d',p);        end        if length(unique(DATA.ArrayConfig.X)) == 1 %linear array, don't need co-ordinates            str = sprintf('%s',pstr);        elseif(mean(abs(diff(DATA.ArrayConfig.Y)))) < 1            str = sprintf('%s(%.1f,%.1f)',pstr,DATA.ArrayConfig.X(p),DATA.ArrayConfig.Y(p));        else            str = sprintf('%s(%.0f,%.0f)',pstr,DATA.ArrayConfig.X(p),DATA.ArrayConfig.Y(p));        end    elseif length(unique(p)) == 1        str = sprintf('%d',mean(p));    else        str = sprintf('%.1f',mean(p));    end            