function RateMenu(src, ks, fcn)DATA = GetDataFromFig(src);Expts = getappdata(DATA.toplevel,'Expts');D = get(gcf,'UserData');f = gcf;    if strcmp(fcn,'hzoomout')        PC.RateZoom(DATA, 3,DATA.currentcell);    elseif strcmp(fcn,'vzoomout')        PC.RateZoom(DATA, 2,DATA.currentcell);    elseif strcmp(fcn,'zoomin')        PC.RateZoom(DATA, 1,DATA.currentcell);    elseif strcmp(fcn,'spool')        PC.SpoolSpikes(DATA, DATA.currentpoint);    elseif strcmp(fcn,'xyseq')        PC.PlotXYSequence(DATA, DATA.currentpoint);    end    figure(f);    