function DATA = RateZoom(DATA,inout, cell)%DATA = PC.RateZoom(DATA,inout, cell)% inout = 1 one cell/one expt% inout = 2 zoom out Y axis only% inout = 3 zoom out all the way X axis onl%y% inout = 5 one cell, all expts    D = get(gcf,'UserData');    eid = DATA.currentpoint(1);    if ~isfield(D,'handles')        return;    end    if cell > length(D.handles)        th = title(sprintf('Expt%d %s cell %d No Spikes',eid,DATA.expnames{eid},cell));        return;    else        h = D.handles(cell);    end    if isfield(D,'cllabel') && D.cllabel > 0 && ishandle(D.cllabel)        delete(D.cllabel);    end    [a,b,cl] = PC.FindCell(DATA, cell, eid);    if isempty(cl)        cl = 0;    else    DATA.currentpoint(2) = b;    DATA.currentcellcluster = cl;    end    th = title(sprintf('Expt%d %s cell %d ',eid,DATA.expnames{eid},cell),'color','k');    if inout == 1 || inout == 5%zoom in Y axis        if inout == 5            set(gca,'xlim',D.xrange);        end        [yl, a] = PC.GetYRange(h, get(gca,'xlim'));        if a.result > 0           set(th,'color',a.color);           if diff(yl) > 0               set(gca,'ylim',yl);           end        else            th = title(sprintf('Expt%d %s cell %d No Spikes',eid,DATA.expnames{eid},cell));            if diff(D.RateRange(cell,:)) > 0                set(gca,'ylim',D.RateRange(cell,:));            end        end    elseif ismember(inout,[2])  %zoom out Y only        yl = minmax(D.RateRange(:));        yls = [];        for j = 1:length(D.handles)            [yl, a] = PC.GetYRange(D.handles(j), get(gca,'xlim'));            if a.result > 0                yls = [yls yl];            end        end        if length(yls)        set(gca,'ylim',minmax(yls));        end    elseif inout == 3        set(gca,'xlim',D.xrange);    elseif inout == 4        set(gca,'ylim',D.yrange);        set(gca,'xlim',D.xrange);    end    %do this after any zooming    D.cllabel = PC.AddClusterString(DATA, th,cl);    set(gcf,'UserData',D);