function ReFit1D(DATA, ratio)      Clusters = getappdata(DATA.toplevel,'Clusters');     [row,col]  = find(DATA.mahal(:,:,1) > 2 & DATA.mahal(:,:,1)./DATA.mahal(:,:,3) > ratio);     for j = 1:length(row)         C = Clusters{row(j)}{col(j)};         [a,b,c] = PC.BestAngleGM(C.xy, C.gmfit2d, []);         DATA.mahal(row(j),col(j),4) = b;         if b < DATA.mahal(row(j),col(j),3);             DATA.mahal(row(j),col(j),4) = b;         end     end     PC.SetFigure(DATA,DATA.tag.clusters);     hold off;     for j = 1:length(row)         plot(DATA.mahal(row(j),col(j),3),DATA.mahal(row(j),col(j),4),'o','buttondownfcn',{@PC.HitPopPoint, row(j),col(j)});         hold on;     end     refline(1);