 function DATA = ReFit3means(DATA, ratio)      Clusters = getappdata(DATA.toplevel,'Clusters');     for j = 1:length(Clusters)         for k = 1:length(Clusters{j})         C = Clusters{j}{k};         a = GMfit(C.xy, 3,1);         DATA.mahal3(j,k) = GMdprime(a);         end     end     PC.SetFigure(DATA,DATA.tag.clusters);     hold off;     for j = 1:length(Clusters)         for k = 1:length(Clusters{j})         plot(DATA.mahal(j,k,1),DATA.mahal3(j,k),'o','buttondownfcn',{@PC.HitPopPoint, row(j),col(j)});         hold on;         end     end     refline(1);     PC.SaveExtras(DATA);     set(DATA.toplevel,'UserData',DATA);   