function DATA = ReadTemplateResults(DATA, nc)for j = 1:length(Clusters)    for k = 1:length(Clusters{j})        DATA.tmplxcs(j,k) = Clusters{j}{k}.cluster{nc}.templatexc;        DATA.tmplscores{j}(k,1:length(Clusters{j}{k}.cluster{nc}.xy)) = Clusters{j}{k}.cluster{nc}.xy(:,3);        DATA.mahal(j,k,1) = Clusters{j}{k}.cluster{nc}.gmdprime;        DATA.mahal(j,k,2) = Clusters{j}{k}.cluster{nc}.mahal(1);    endend