function ReplotXY(a,b,eid, probe,cid)    DATA = GetDataFromFig(a);          if ishandle(a)    cmenu = get(a,'UIContextMenu');    else        cmenu = [];    end    callback = get(gca,'ButtonDownFcn');    axdata = get(gca,'UserData');    bt = strmatch(get(gcf,'SelectionType'),{'normal' 'alternate'  'extend'  'open'});    if bt == 2  && ~isempty(cmenu)        return;    end    if ~isfield(DATA,'elmousept') || DATA.elmousept.down > 0        return;    end    if cid ~= DATA.currentcluster        newcluster = 1;    else        newcluster = 0;    end    DATA.currentcluster = cid;    Clusters = getappdata(DATA.toplevel,'Clusters');    Clusters{eid}{probe} = PC.UpdateWithNewCut(Clusters{eid}{probe}, DATA);    fprintf('Replotting E%dP%dC%d\n',eid,probe,cid);    PC.PlotClusterXY(DATA,Clusters{eid}{probe},'shorttitle','cellid',cid,'tight');    if DATA.plot.trighist        PC.AddTrigHist(DATA,Clusters{eid}{probe},DATA.currentcluster);    end    set(gca,'Xtick',[],'Ytick',[],'ButtonDownFcn',callback,'UserData',axdata);    PC.AddCellLabels(DATA, eid, probe, 'NW');    if newcluster        set(DATA.toplevel,'UserData',DATA);        F = gcf;        if DATA.plot.hist            PC.SetFigure(DATA,DATA.tag.hist,'front');            PC.PlotClusterHistogram(DATA, Clusters{eid}{probe}, DATA.plot.refitgm,'cluster', DATA.currentcluster);            figure(F);        end    end    