function SaveCellList(DATA, varargin)backup = 0;j = 1;while j <= length(varargin)    if strncmpi(varargin{j},'backup',4)        backup = 1;    end    j = j+1;end    cellfile = [DATA.name '/CellList.mat'];    if backup        BackupFile(cellfile);    end    X.CellList = DATA.CellList;    X.CellDetails = DATA.CellDetails;    X.CellChanges = DATA.CellChanges;    if isfield(DATA,'fitjumps')        CellDetails.fitjumps = DATA.fitjumps;    end    if isfield(DATA,'muCellList')        X.muCellList = DATA.muCellList;        sz = size(DATA.CellList);        if size(X.muCellList,1) < size(DATA.CellList,1) || size(X.muCellList,2) < size(DATA.CellList,2)            X.muCellList(sz(1),sz(2),:) = 0;        end    end    save(cellfile, '-struct','X');