function DATA = SetAllXYClustering(DATA, onoff)     F = PC.SetFigure(DATA,DATA.tag.allxy);    if onoff    set(F, 'WindowButtonDownFcn',@PC.XYButtonPressed);    set(F, 'WindowButtonMotionFcn',@PC.XYButtonDragged);    set(F, 'WindowButtonUpFcn',@PC.XYButtonReleased);    set(F, 'WindowScrollWheelFcn',@PC.ScrollWheel);    set(F,'UserData',DATA.toplevel);    set(F,'keypressfcn',@PC.XYKeyPressed);    else        c = get(F,'children');    end    DATA.allclustering = onoff;    