function DATA = SetExpt(mn,b,  fcn)    DATA = GetDataFromFig(mn);    nex = length(DATA.strings);    e = get(mn,'value');    DATA.currentpoint(1) = e;    PC.ShowData(DATA,DATA.currentpoint(1),DATA.currentpoint(2),'oneprobe');    PC.PrintComments(DATA,e,1:DATA.nprobes);    if DATA.plot.allxy        PC.PlotAllProbeXY(DATA,'expt',DATA.currentpoint(1));    end    DATA = PC.ClearSelections(DATA,0,0);    if DATA.spoolspikes        PC.PlotAllProbe(DATA,'spoolall');    elseif DATA.plot.quickspks        PC.PlotAllProbe(DATA,'allspks');    end    str = sprintf('Ex %.1f: %s',DATA.exptid(DATA.currentpoint(1)),DATA.expnames{DATA.currentpoint(1)});    fprintf('%s\n',str);    PC.SetFigure(DATA, DATA.tag.all);    title(str);    Expts = getappdata(DATA.toplevel,'Expts');    DATA.Expt = Expts{e};    PC.SetTrialList(DATA,{}, DATA.currenttrial);    stopped = 0;     DATA = PC.MarkCurrentCluster(DATA);set(DATA.toplevel,'UserData',DATA);