function Expts = SetExptTimeOffset(Expts)    if length(Expts)        ctimes = [];        for j = 1:length(Expts)            if isfield(Expts{j},'Header')                ctimes(j) = Expts{j}.Header.CreationDate;            end        end        estart = min(ctimes(ctimes > 0));        for j = 1:length(Expts)            if isfield(Expts{j},'Header')                Expts{j}.Header.timeoffset = (ctimes(j)-estart).*24.*60.*60;            end        end    end