function DATA = SetProbe(mn,b, dir)    DATA = GetDataFromFig(mn);    nex = length(DATA.strings);        p = get(mn,'value');    DATA.currentpoint(2) = p;    PC.ShowData(DATA,DATA.currentpoint(1),DATA.currentpoint(2),'oneprobe');    if DATA.plot.allxy        PC.PlotAllProbeXY(DATA,'oneprobe');    end    if DATA.plot.quickspks        DATA = PC.ClearSelections(DATA,0,0);        PC.PlotAllProbe(DATA,'allprobespks');    end    DATA = PC.MarkCurrentCluster(DATA);set(DATA.toplevel,'UserData',DATA);