function SpikeDistances(DATA, type)spk = 1;ids = [];PC.SetFigure(DATA, DATA.tag.clusters,'front');clf;for j = 1:length(DATA.strings)    [sizes(j,:,:), ds(j,:,:), quality(j,:)] = PC.SpikeDistance(DATA, j);enddval = unique(ds(:));for j = 1:length(dval)    id = find(ds == dval(j));    ms(j) = mean(sizes(id));endhold off;plot(dval, ms,'o-');for q = [-2 2 4 6][rows, cols]= find(quality.* sign(q) > q);for j = 1:length(rows)    d(j,:) = ds(rows(j),cols(j),:);    s(j,:) = sizes(rows(j),cols(j),:);endfor j = 1:length(dval)    id = find(d == dval(j));    ms(j) = mean(s(id));endhold on;plot(dval, ms,'ro-');end