function DATA = SpoolCurrentSpikes(mn,b)    DATA = GetDataFromFig(mn);    AllSpikes = PC.CheckAllSpikes(DATA,DATA.currentpoint(1),DATA.currentpoint(2),'allprobes');    DATA = get(DATA.toplevel,'UserData');        if isempty(AllSpikes)        a = PC.SpoolSpikeFile(DATA,DATA.currentpoint(1),DATA.currentpoint(2));        if isfield(a,'svfig')            DATA.spoolfig = a.svfig;            set(DATA.toplevel,'UserData',DATA);        end    else        Clusters = getappdata(DATA.toplevel,'Clusters');        PC.SetFigure(DATA,DATA.tag.xyplot,'front');        hold off;        plots = PC.PlotClusterXY(DATA,Clusters{DATA.currentpoint(1)}{DATA.currentpoint(2)});        drawnow;        PC.SetTrialList(DATA,Clusters{DATA.currentpoint(1)}{DATA.currentpoint(2)},DATA.currenttrial);        PC.SpoolSpikes(DATA, DATA.currentpoint);    end            