function res = SpoolSpikeFile(DATA,e,p)            args = {};    Clusters = getappdata(DATA.toplevel,'Clusters');    Expts = getappdata(DATA.toplevel,'Expts');    if DATA.NewCut.exptid == e && isfield(DATA.NewCut,'clst')        C = DATA.NewCut;    else    C = Clusters{e}{p};    end    xs = '';    if rem(C.exptno,1) > 0.001        xs = 'a';    end    [a,b] = fileparts(DATA.name);    [c,d] = fileparts(a);    if DATA.nprobespool > 0        chspk = p-DATA.nprobespool:p+DATA.nprobespool;        chspk = chspk(chspk > 0 & chspk <=24 & chspk ~= p);%        chspk = [p+1];        for j = 1:length(chspk)            args = {args{:} 'spkfile' [DATA.name '/Spikes/' b '.p' num2str(chspk(j))  't' num2str(floor(C.exptno)) xs '.mat']};        end    end    name = [DATA.name '/Spikes/' b '.p' num2str(p)  't' num2str(floor(C.exptno)) xs '.mat'];    xcl = PC.FindExcludedTrials(DATA,e,p,1, C);    fprintf('Spooling Expt %d(row %d) Probe %d\n',C.exptno,e,p);    if max(xcl) > length(Expts{e,p}.Trials)        res = PlotSpikeFile(name,'Trials',Expts{e,p},args{:},C);    else        res = PlotSpikeFile(name,'Trials',Expts{e,p},'exclude',xcl,args{:},C);    end