function Update(a,b)DATA = GetDataFromFig(a);DATA.spoolspikes = PC.GetCheck('SpoolSpikes',DATA.toplevel);DATA.plot.spkxy = PC.GetCheck('SpikeXY',DATA.toplevel);DATA.plot.spkmean = PC.GetCheck('SpikeMean',DATA.toplevel);%plotexpt = PC.GetCheck('PlotExpt',DATA.toplevel);%if plotexpt && strcmp(DATA.plotexpttype,'none')  %  DATA.plotexpttype = 'means';%elseif plotexpt == 0%    DATA.plotexpttype = 'none';%endDATA.plot.refitgm = PC.GetCheck('RefitGM',DATA.toplevel);DATA.plot.gmfit = PC.GetCheck('ShowGM',DATA.toplevel);DATA.plot.allxy = PC.GetCheck('PlotAllXY',DATA.toplevel);DATA.plot.xyseq = PC.GetCheck('PlotXYseq',DATA.toplevel);DATA.plot.trighist = PC.GetCheck('PlotTrigHist',DATA.toplevel);DATA.plot.quickspks = PC.GetCheck('PlotSpks',DATA.toplevel);DATA.plot.hist = PC.GetCheck('PlotHistogram',DATA.toplevel);DATA.exclude.offpeak = PC.GetCheck('PeakMismatch',DATA.toplevel);DATA.exclude.onpeak = PC.GetCheck('PeakMatch',DATA.toplevel);DATA.exclude.noncell = PC.GetCheck('NonCell',DATA.toplevel);DATA.colorscheme = PC.GetPopString('ColorScheme',DATA.toplevel);DATA.show.exptno = PC.GetCheck('LabelExptno',DATA.toplevel);DATA.show.exptname = PC.GetCheck('LabelExptName',DATA.toplevel);DATA.show.ed = PC.GetCheck('LabelEd',DATA.toplevel);DATA.show.allvpcs = PC.GetCheck('AllVPcs',DATA.toplevel);set(DATA.toplevel,'UserData',DATA);PC.SetGUI(DATA);